mapper.select:
SELECT count(*)
FROM sec_user_group_meb
WHERE groupId = #{groupId}

SELECT
*
FROM sec_user_relation
WHERE
userId = #{userId}
and toUserId = #{toUserId}

mapper.update:
UPDATE sec_user_relation
state = #{state}, applyTime = #{applyTime}, type = #{type}, actionTime = now()
WHERE id = #{id}
