mapper.select:
SELECT count(*)
FROM sec_user_group
WHERE userId = #{userId}

mapper.insert:
INSERT INTO sec_user_group_meb (groupId, userId, createTime) VALUES
(#{bean.groupId}, #{bean.userId}, now())
ON DUPLICATE KEY UPDATE groupId=VALUES(groupId), userId=VALUES(userId);

mapper.update:
UPDATE sec_user_group
name = #{name}, icon = #{icon}, num = #{num},
WHERE id = #{id}

mapper.delete:
delete from sec_user_group_meb
where userId in
(

	#{item.userId}

)
and groupId in
(

	#{item.groupId}

)
