mapper.select:
select count(*)
from sec_message


		type = #{type}

	and tuid = #{tuid}

		and m.loginPort = #{loginPort}

select m.*
from sec_message m


		m.type = #{type}

	and m.tuid = #{tuid}

		and m.loginPort = #{loginPort}


order by m.addTime DESC

select * from sec_message
and mid = #{mid}
order by addTime DESC

select count(*)
from sec_message


		type = #{type}

	and tuid = #{tuid}
	and isRead = 0

mapper.update:
UPDATE sec_message

	isRead = 1



		type = #{type}

	and tuid = #{tuid}
	and isRead = 0
