mapper.select:
SELECT count(*) FROM sec_user_addr
userId = #{userId}
and showType = 1 and state = 1

mapper.update:
UPDATE sec_user_addr
type = #{type}, plotId = #{plotId}, conPhone = #{conPhone}, conName = #{conName}, ext = #{ext}, district = #{district}, addr = #{addr}, state = #{state}, isDef = #{isDef}, showType = #{showType},
WHERE id = #{id}
